gene=""

def write_fasta(pos1, pos2, gene):
	fasta_name="exon__%d_%d.fa" % (pos1,pos2)
	exon=gene[pos1:pos2]
	f=open(fasta_name, "a")
	f.write("> %s\n%s" % (fasta_name, exon))
	f.close()

write_fasta(0,509,gene)
write_fasta(1400,1630,gene)
write_fasta(2298,2537,gene)
write_fasta(2940,3081,gene)
write_fasta(3671,5120,gene)
